import { useState } from "react";

function App() {

    const [count, SetCount] = useState(0);






    const minus = () => {

        SetCount(-1);

    }


    const plus = () => {

        SetCount(count + 1);

    };



    return (

        <div>
            <button onClick={minus}>-</button>

            <p>{SetCount}</p>

            <button onClick={plus}>+</button>

        </div>
    );
}


export default App
