import style from './mainButton.module.css'
const MainButton = () => {
    const { text = 'click me' } = props;
    const getColor = () => {
        switch (color) {
            case "dark":
                return style.main;
            case "red":
                return style.main;
            case "yellow":
                return style.main;
            case "white":
                return style.main;
            default:
                return style.main
        }
    }
    return <button className={'${style.main} ${style.main} ${style.normal'}>{text}</button>
};

export default MainButton;
